DROP TABLE IF EXISTS `me_tags`;
CREATE TABLE `me_tags` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '标签字段',
  `tagname` varchar(255) DEFAULT NULL COMMENT '标签名称',
  `plug` varchar(255) DEFAULT 'System' COMMENT 'System',
  `unit` varchar(255) DEFAULT '' COMMENT '单位',
  `value` varchar(255) DEFAULT '' COMMENT '值',
  `min` int(11) DEFAULT '0' COMMENT '下限',
  `max` int(11) DEFAULT '1000' COMMENT '上限',
  `time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '时间',
  `desc` varchar(255) DEFAULT '' COMMENT '标签描述',
  `statu` int(2) DEFAULT '1' COMMENT '状态',
  `alarm` int(11) DEFAULT '0' COMMENT '告警',
  `digcount` int(11) DEFAULT '0',
  `type` varchar(20) DEFAULT 'String' COMMENT '类型',
