  `ah` int(10) DEFAULT '80' COMMENT '高报',
  `ahh` int(10) DEFAULT '100' COMMENT '高高报',
  `all` int(10) DEFAULT '0' COMMENT '低低报',
  `al` int(10) DEFAULT '10' COMMENT '低报',
  `ad` int(10) DEFAULT '0',
  `datasetid` int(11) DEFAULT NULL COMMENT '数据集id',
  `uid` int(11) DEFAULT NULL COMMENT '用户id',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `datasetid` (`datasetid`) USING BTREE,
  KEY `uid` (`uid`) USING BTREE,
